import os
from sqlite3 import connect

class BaseDatos():
    def __init__(self) -> None:
        self.Crear_Tablas()
    ################################# CONECTAR BASE DE DATOS
    def conectar(self):
        con = connect('modulos/BasesDatos/DataBase.db')
        return con,con.cursor()

    ################################# CREAR TABLAS
    def Crear_Tablas(self):
        if os.path.exists('modulos/BasesDatos/DataBase.db') ==False:
            con,cursor=self.conectar()     
            cursor.execute("CREATE TABLE IF NOT EXISTS TRANSMISION(ID_TRANSMISION INTEGER PRIMARY KEY, TIPO TEXT, DESCRIPCION TEXT)")
            cursor.execute("CREATE TABLE IF NOT EXISTS DEPARTAMENTOS(ID_DEPARTAMENTO INTEGER PRIMARY KEY AUTOINCREMENT, NOMBRE TEXT, DESCRIPCION TEXT)")
            cursor.execute("CREATE TABLE IF NOT EXISTS UNIDADES_INTERNET(ID_UNIDAD TEXT PRIMARY KEY, NOMBRE_UNIDAD TEXT,CORD TEXT, SERVICIO_UNIDAD TEXT, PROVEEDOR_UNIDAD TEXT, OBSERVACIONES TEXT, FECHA_UNIDAD TEXT, TEL_UNIDAD TEXT, ID_TRANSMISION INTEGER, FOREIGN KEY (ID_TRANSMISION) REFERENCES TRANSMISION(ID_TRANSMISION) ON UPDATE CASCADE ON DELETE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS PROVEEDOR(ID_PROVEEDOR INTEGER PRIMARY KEY AUTOINCREMENT, NOMBRE TEXT, DESCRIPCION TEXT, FECHA TEXT, TELEFONO TEXT,ID_TRANSMISION INTEGER, FOREIGN KEY (ID_TRANSMISION) REFERENCES TRANSMISION(ID_TRANSMISION) ON UPDATE CASCADE ON DELETE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS REPORTES(ID_REPORTE INTEGER PRIMARY KEY AUTOINCREMENT, FOLIO TEXT, DESCRIPCION TEXT, FECHA TEXT, ACUDIO TEXT, REMOTO_SITIO TEXT,MOTIVO TEXT, TELEFONO TEXT, ATENDIO TEXT, ID_PROVEEDOR INTEGER, FOREIGN KEY (ID_PROVEEDOR) REFERENCES PROVEEDOR(ID_PROVEEDOR) ON UPDATE CASCADE ON DELETE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS RED(ID_RED INTEGER PRIMARY KEY AUTOINCREMENT, SSID TEXT, PASSWORD TEXT, SUBIDA REAL, BAJADA REAL, ID_PROVEEDOR INTEGER, FOREIGN KEY (ID_PROVEEDOR) REFERENCES PROVEEDOR(ID_PROVEEDOR) ON UPDATE CASCADE ON DELETE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS HISTORIAL_NUM_CONEXIONES(ID_HISTORIAL_NUM_CONEXIONES INTEGER PRIMARY KEY AUTOINCREMENT, ID_RED INTEGER,FECHA TEXT,NUMERO INT, FOREIGN KEY (ID_RED) REFERENCES RED (ID_RED) ON UPDATE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS HISTORIAL_CONEXIONES(ID_HISTORIAL_CONEXIONES INTEGER PRIMARY KEY AUTOINCREMENT,FECHA TEXT,ID_RED INTEGER, NOMBRE_HOST TEXT, IP_HOST TEXT, MAC_HOST TEXT, VENDOR TEXT, FOREIGN KEY (ID_RED) REFERENCES RED(ID_RED))")
            cursor.execute("CREATE TABLE IF NOT EXISTS CONTROL_RED_ESPECIFICA(ID_CONTROL_RED_ESPECIFICA INTEGER PRIMARY KEY AUTOINCREMENT, ID_RED INTEGER, IP_HOST TEXT, NO_INV TEXT, STATUS TEXT, MAC_HOST TEXT, USUARIO TEXT, FECHA TEXT, OBSERVACIONES TEXT, USUARIO_DIS TEXT,VERIFICADO TEXT,TIPO TEXT, ID_DEPARTAMENTO INT, VENDOR TEXT,  FOREIGN KEY (ID_RED) REFERENCES RED(ID_RED) ON UPDATE CASCADE, FOREIGN KEY (ID_DEPARTAMENTO) REFERENCES DEPARTAMENTOS (ID_DEPARTAMENTO) ON UPDATE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS PRUEBAS_VELOCIDAD(ID_PRUEBAS_VELOCIDAD INTEGER PRIMARY KEY AUTOINCREMENT, ID_RED INTEGER, FECHA TEXT, HORA TEXT,HOST TEXT, SPONSOR TEXT,SUBIDA FLOAT, BAJADA FLOAT,PINGS FLOAT, PINGB FLOAT, PING FLOAT, RESULTADO TEXT, FOREIGN KEY (ID_RED) REFERENCES RED(ID_RED) ON UPDATE CASCADE)")
            cursor.execute("CREATE TABLE IF NOT EXISTS DISPOSITIVOS(ID_DISPOSITIVO INTEGER PRIMARY KEY AUTOINCREMENT,NOINV TEXT, NOMBRE TEXT, DESCRIPCION TEXT, UBICACION TEXT, TIPO_CONEXION TEXT, ID_RED INTEGER, ID_DEPARTAMENTO INTEGER, FOREIGN KEY (ID_RED) REFERENCES RED(ID_RED) ON UPDATE CASCADE,  FOREIGN KEY (ID_DEPARTAMENTO) REFERENCES DEPARTAMENTOS (ID_DEPARTAMENTO) ON UPDATE CASCADE)")
