    ############## CONSULTAS
    def consultar(self,CONSULTA):
        con,cursor=self.conectar()
        cursor.execute(CONSULTA)
        datos = cursor.fetchall()
        cursor.close()
        con.close()
        return datos
    def consultarTRANSMISION(self):
        return self.consultar("SELECT * FROM TRANSMISION")
    def consultarDEPARTAMENTOS(self):
        return self.consultar("SELECT * FROM DEPARTAMENTOS")
    def consultarUNIDADES_INTERNET(self):
        return self.consultar("SELECT P.ID_UNIDAD, P.NOMBRE_UNIDAD, P.CORD,P.SERVICIO_UNIDAD, P.PROVEEDOR_UNIDAD, P.OBSERVACIONES, P.FECHA_UNIDAD, P.TEL_UNIDAD, T.TIPO FROM UNIDADES_INTERNET AS P INNER JOIN TRANSMISION T ON T.ID_TRANSMISION=P.ID_TRANSMISION")
    def consultarPROVEEDOR(self):
        return self.consultar("SELECT P.ID_PROVEEDOR, P.NOMBRE, P.DESCRIPCION, P.FECHA,P.TELEFONO, T.TIPO  FROM PROVEEDOR AS P INNER JOIN TRANSMISION T ON T.ID_TRANSMISION=P.ID_TRANSMISION")
    def consultarREPORTES(self):
        return self.consultar("SELECT R.ID_REPORTE, R.FOLIO, R.DESCRIPCION, R.FECHA, R.ACUDIO, R.REMOTO_SITIO,R.MOTIVO, R.TELEFONO, R.ATENDIO,  P.NOMBRE FROM REPORTES AS R INNER JOIN PROVEEDOR AS P ON P.ID_PROVEEDOR=R.ID_PROVEEDOR")
    def consultarRED(self):
        return self.consultar("SELECT R.ID_RED, R.SSID, R.PASSWORD, R.SUBIDA, R.BAJADA, P.NOMBRE FROM RED AS R INNER JOIN PROVEEDOR AS P ON R.ID_PROVEEDOR=P.ID_PROVEEDOR")
    def consultarHISTORIAL_NUM_CONEXIONES(self):
        return self.consultar("SELECT H.ID_HISTORIAL_NUM_CONEXIONES,R.SSID,H.FECHA,H.NUMERO FROM HISTORIAL_NUM_CONEXIONES AS H INNER JOIN RED AS R ON R.ID_RED=H.ID_RED")
    def consultarHISTORIAL_CONEXIONES(self):
        return self.consultar("SELECT H.ID_HISTORIAL_CONEXIONES,H.FECHA,R.SSID, H.NOMBRE_HOST, H.IP_HOST, H.MAC_HOST, H.VENDOR FROM HISTORIAL_CONEXIONES AS H INNER JOIN RED AS R ON R.ID_RED=H.ID_RED")
    def consultarCONTROL_RED_ESPECIFICA(self):
        return self.consultar("SELECT C.ID_CONTROL_RED_ESPECIFICA, C.FECHA, R.SSID,C.NO_INV, C.STATUS, C.USUARIO_DIS, C.IP_HOST,C.VENDOR, C.MAC_HOST, C.USUARIO, C.OBSERVACIONES, C.VERIFICADO,C.TIPO,D.NOMBRE FROM CONTROL_RED_ESPECIFICA AS C INNER JOIN RED AS R ON R.ID_RED=C.ID_RED INNER JOIN DEPARTAMENTOS AS D ON D.ID_DEPARTAMENTO=C.ID_DEPARTAMENTO")
    def consultarPRUEBAS_VELOCIDAD(self):
        return self.consultar("SELECT P.ID_PRUEBAS_VELOCIDAD,P.FECHA,P.HORA,P.HOST,P.SPONSOR,R.SSID,P.SUBIDA, P.BAJADA,P.PINGS,P.PINGB,P.PING, P.RESULTADO FROM PRUEBAS_VELOCIDAD AS P INNER JOIN RED AS R ON R.ID_RED=P.ID_RED ")
    def consultarDISPOSITIVOS(self):
        return self.consultar("SELECT D.ID_DISPOSITIVO, D.NOINV, D.NOMBRE, D.DESCRIPCION, D.UBICACION, D.TIPO_CONEXION, R.SSID,DE.NOMBRE FROM DISPOSITIVOS AS D INNER JOIN RED AS R ON R.ID_RED=D.ID_RED INNER JOIN DEPARTAMENTOS AS DE ON DE.ID_DEPARTAMENTO=D.ID_DEPARTAMENTO" )
    def consultarPROBLEMAS_RED(self):
        return self.consultar("SELECT D.ID_PROBLEMA, D.DESCRIPCION, D.FECHA, D.SOLUCION, D.CORREGIDO, D.FECHA_CORRECCION, R.SSID,DE.NOMBRE FROM PROBLEMAS_RED AS D INNER JOIN RED AS R ON R.ID_RED=D.ID_RED INNER JOIN DEPARTAMENTOS AS DE ON DE.ID_DEPARTAMENTO=D.ID_DEPARTAMENTO")